init:
const buttons = {
    'up': 'D-Pad Вверх - перемещение вверх',
    'down': 'D-Pad Вниз - перемещение вниз',
    'left': 'D-Pad Влево - перемещение влево',
    'right': 'D-Pad Вправо - перемещение вправо',
    'a': 'Кнопка A - основное действие',
    'b': 'Кнопка B - второстепенное действие',
    'start': 'START - пауза/меню',
    'select': 'SELECT - дополнительная функция'
};

let currentButton = 'up';

function render() {
    document.getElementById('game-container').innerHTML = `
        <div style="text-align: center; padding: 20px;">
            <h2>${currentButton.toUpperCase()}</h2>
            <div style="font-size: 24px; margin: 30px 0;">
                ${buttons[currentButton]}
            </div>
            <div style="margin-top: 40px;">
                Нажмите любую кнопку чтобы увидеть её описание
            </div>
            <button onclick="backToMainMenu()" style="margin-top: 30px;">В меню</button>
        </div>
    `;
}

render();
